@section('scripts')
<script src="{{ url("/") }}/js/reflection.js"></script>

<script>
    var type = "<?php 
    if (sizeof($types) > 0) {
        echo $types[0];
    } else {
        echo "";
    }
            ?>";
    
    $(window).on('hashchange', function() {
        if (window.location.hash) {
            var page = window.location.hash.replace('#', '');
            if (page == Number.NaN || page <= 0) {
                return false;
            } else {
                getVehicles(page, type);
            }
        }
    });

    $(document).ready(function() {
        $(document).on('click', '.nav-pills li a', function (e) {
            var hash = $(this).attr('href').replace('#', '');
            type = hash;
            e.preventDefault();
        });
        $(document).on('click', '.pagination a', function (e) {
            getVehicles($(this).attr('href').split('page=')[1], type);
            e.preventDefault();
        });
    });
    
    function getVehicles(page, type) {
        $.ajax({
            url : '?page=' + page + '&type=' + type,
            dataType: 'json'
        }).done(function (data) {
            $('#' + type).html(data);
            location.hash = page;
        }).fail(function () {
            alert(type.replace("_", " ") + ' could not be loaded.');
        });
    }
    
